import "./TotalMoney.css";

export const TotalMoney = ({ listTransactions }) => {
  const totalMoney = listTransactions.reduce((acc = 0, act) => listTransactions.type === "Entrada" ? (acc + +act.value) : (acc - +act.value));

  return (
    <section className="total-money">
      <div className="money-block">
        <p className="total-text">Valor Total:</p>
        <span className="money-span">Valor referente ao saldo</span>
      </div>
      <span className="total-value">{totalMoney.toLocaleString('pt-br',{style: 'currency', currency: 'BRL'})}</span>
    </section>
  );
};
