import trash from "../../assets/trash.svg";
import "./Card.css";

export const Card = (transaction, index) => {

  const value = transaction.transaction.value
  console.log(value)

  return (
    <>
      <div className="Card" key={index}>
        <div className="Card-block-1">
          <h2 className="Card-description">{transaction.transaction.description}</h2>
          <span className="Card-type">{transaction.transaction.type}</span>
        </div>
        <div className="Card-block-2">
          <span className="Card-value">{transaction.transaction.value.toLocaleString("pt-BR", { minimumFractionDigits: 2 , style: 'currency', currency: 'BRL' })}</span>
          <img className="Card-trash" src={trash} alt="lixeira" />
        </div>
      </div>
    </>
  );
};
