import { Card } from "../Card/Card";
import NoCard from "../../assets/NoCard.svg";
import "./List.css";

export const List = ({ listTransactions, setListTransactions}) => {

  const teste = () => {

    const financialFilter = listTransactions.filter(transaction => {
      if(transaction.type === "Entrada"){
        return transaction
      }else if( transaction.type === "Saída"){
        return transaction
      }
    })
    // console.log(financialFilter)
    setListTransactions(financialFilter)
  }



  return (
    <section className="List-section">
      <div className="sumary-block">
        <p className="financial-sumary">Resumo financeiro</p>
        <div className="button-sumary">
          <button className="button-default">Todos</button>
          <button className="button-default" onClick={() => teste()}>Entradas</button>
          <button className="button-default">Despesas</button>
        </div>
      </div>
      {listTransactions.length === 0 ? (
        <>
          <p className="transactions-blank"> Você ainda não possui nenhum lançamento </p>
          <img className="transactions-blank-img" src={NoCard} alt="" />
        </>
      ) : (
        <>
          <ul className="List-transactions">
            {listTransactions.map((transaction, index) => (
              <li className="item-transactions">
                {transaction.type === "Entrada" ? (
                  <div className="deposit"></div>
                ) : (
                  <div className="out"></div>
                )}
                <Card transaction={transaction} key={index} />
              </li>
            ))}
          </ul>
        </>
      )}
    </section>
  );
};
